package kubeconfig

import (
	"io/ioutil"
	"os"
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestSetServer(t *testing.T) {
	var kubeconfig = `apiVersion: v1
clusters:
- cluster:
    certificate-authority-data: 
    server: https://10.128.0.3:6443
  name: kubernetes
contexts:
- context:
    cluster: kubernetes
    user: kubernetes-admin
  name: kubernetes-admin@kubernetes
current-context: kubernetes-admin@kubernetes
kind: Config
preferences: {}
users:
- name: kubernetes-admin
  user:
    client-certificate-data: 
    client-key-data: 
`
	f, err := os.CreateTemp("", "")
	if err != nil {
		t.Fatal(err)
		return
	}
	defer f.Close()

	if _, err := f.Write([]byte(kubeconfig)); err != nil {
		t.Fatal(err)
		return
	}

	if err := SetServer(f.Name(), "https://localhost:6444"); err != nil {
		t.Fatal(err)
		return
	}

	got, err := ioutil.ReadFile(f.Name())
	if err != nil {
		t.Fatal(err)
		return
	}

	var expect = `apiVersion: v1
clusters:
- cluster:
    certificate-authority-data: 
    server: https://localhost:6444
  name: kubernetes
contexts:
- context:
    cluster: kubernetes
    user: kubernetes-admin
  name: kubernetes-admin@kubernetes
current-context: kubernetes-admin@kubernetes
kind: Config
preferences: {}
users:
- name: kubernetes-admin
  user:
    client-certificate-data: 
    client-key-data: 
`
	assert.Equal(t, expect, string(got))
}
